mapper.select:
SELECT
  count(c.id)
FROM
  class c
WHERE 1=1 AND c.school_id=#{searchs.schoolId}

    AND c.id = #{searchs.classId}

SELECT c.id, c.title, c.class_name, c.school_id, s.title school_name, c.title, a.id AS teacher_id, a.nick_name AS teacher_name, a.username AS account_username, IF(c.teacher_id=a.id,1,-1) AS is_head FROM class c LEFT JOIN school s ON c.school_id = s.id LEFT JOIN account_class acc ON acc.class_id=c.id LEFT JOIN account a ON acc.account_id=a.id WHERE 1=1 AND c.school_id=#{searchs.schoolId}
AND c.id = #{searchs.classId}
AND c.id = #{searchs.classId}
AND c.id IN( SELECT a.id FROM (SELECT DISTINCT id FROM class WHERE school_id=#{searchs.schoolId} LIMIT #{startIndex}, #{pageSize}) a )
${item.field} ${item['type']}

SELECT id, class_id, timetable, start_date FROM timetable WHERE 1=1

    AND class_id = #{searchs.classId}

LIMIT #{startIndex}, #{pageSize}

SELECT id, class_id, content, img_path, img_url FROM class_content WHERE 1=1

    AND class_id = #{searchs.classId}

LIMIT #{startIndex}, #{pageSize}

SELECT
    c.id,
    c.title
FROM
    account_class ac
JOIN class c ON ac.class_id = c.id

    AND ac.account_id = #{searchs.accountId}


    ${item.field} ${item['type']}

LIMIT #{startIndex}, #{pageSize}
