SELECT c.id, c.title, c.school_id, s.title school_name, c.teacher_id, t.teacher_name FROM class c LEFT JOIN school s ON c.school_id = s.id LEFT JOIN teacher t ON c.teacher_id = t.id
AND class_id = #{searchs.classId}
${item.field} ${item['type']}
LIMIT ${startIndex}, ${pageSize}

SELECT id, class_id, timetable, start_date FROM timetable WHERE 1=1

   AND class_id = #{searchs.classId}

LIMIT #{startIndex}, #{pageSize}

