mapper.select:
SELECT COUNT(notice_id) FROM notice WHERE 1=1

    AND class_id=#{searchs.classId}


    AND from_to = #{searchs.formTo}

GROUP BY notice_id

SELECT notice_title, notice_content, notice_date,from_to,notice_file,GROUP_CONCAT(class_id SEPARATOR '|') AS classIdSplit
FROM notice WHERE 1=1

    AND class_id=#{searchs.classId}


    AND from_to = #{searchs.formTo}


    ${item.field} ${item['type']}

GROUP BY notice_id,notice_title, notice_content, notice_date,from_to
LIMIT #{startIndex}, #{pageSize}

SELECT notice_title, notice_content, notice_date,from_to, is_read,notice_file
FROM notice
WHERE 1=1
AND class_id=#{searchs.classId}

    AND from_to = #{searchs.formTo}


    ${item.field} ${item['type']}

LIMIT #{startIndex}, #{pageSize}

select id, content, img_path, img_url, publish_date from daily_recommend where 1=1

    AND type = #{searchs.type}

order by publish_date desc
LIMIT ${startIndex}, ${pageSize}
