mapper.select:
SELECT
      count(ar.id)
  FROM
      alarm_report ar
  LEFT JOIN students s ON ar.student_id = s.id
  LEFT JOIN class c ON s.class_id = c.id
  WHERE 1=1

      and ar.alarm_time BETWEEN #{searchs.alarmBeginTime} and #{searchs.alarmEndTime}


      and c.id = #{searchs.classId}


      and ar.alarm_type = #{searchs.alarmType}


      and ar.student_id = #{searchs.studentId}

SELECT ar.id, s.card_no, s.`name` AS studentName, c.title AS className, ar.alarm_time, ar.alarm_data, ar.alarm_type, ar.student_id, ar.longitude, ar.latitude FROM alarm_report ar LEFT JOIN students s ON ar.student_id = s.id LEFT JOIN class c ON s.class_id = c.id WHERE 1=1
and ar.alarm_time BETWEEN #{searchs.alarmBeginTime} and #{searchs.alarmEndTime}
and c.id = #{searchs.classId}
and ar.alarm_type = #{searchs.alarmType}
and ar.student_id = #{searchs.studentId}
