mapper.select:
SELECT s.id, s.title, s.simple_title, DATE_FORMAT(s.registe_date, "%Y-%m-%d") registe_date, s.linkman, s.phone, s.area, s.address, s.lon, s.lat, s.email, s.description, agent_id as agentId, m.realname as agentName, province, city, village FROM school s LEFT JOIN manager m ON s.agent_id = m.id WHERE 1 = 1
and s.title like CONCAT('%',#{searchs.title},'%')
and s.area like CONCAT('%',#{searchs.area},'%')
${item.field} ${item['type']}
LIMIT #{startIndex}, #{pageSize}

SELECT id, title, simple_title, registe_date, linkman, phone, area, address, lon, lat, email, description FROM school s WHERE 1 = 1
and s.id = #{id}
and s.area = #{area}
and s.title = #{title}

SELECT c.id, c.class_name, st.student_count, st.device_count FROM class c LEFT JOIN ( SELECT class_id, count(s.id) student_count, count(s.ring_no) device_count FROM students s WHERE 1=1
and s.school_id = #{searchs.schoolId}
GROUP BY class_id ) st ON c.id = st.class_id WHERE 1=1
and c.school_id = #{searchs.schoolId}
LIMIT #{startIndex}, #{pageSize}

SELECT s.id AS schoolId, s.title AS schoolName, c.class_count AS classCount, stu.student_count AS studentCount, stu.device_count AS deviceCount FROM school s LEFT JOIN ( SELECT school_id, count(*) class_count FROM class GROUP BY school_id ) c ON s.id = c.school_id LEFT JOIN ( SELECT school_id, count(id) student_count, count(ring_no) device_count FROM students GROUP BY school_id ) stu ON s.id = stu.school_id WHERE 1=1
and s.id = #{searchs.schoolId}
LIMIT #{startIndex}, #{pageSize}

mapper.update:
update students set school_id = null where school_id IN

    #{schoolId}

update class set school_id = null where school_id IN

    #{schoolId}
