mapper.select:
SELECT count(s.id) FROM
    students s
    LEFT JOIN class c ON s.class_id = c.id
where delete_flag = 0

    and s.`name` like CONCAT('%',#{searchs.studentName},'%')


    and s.register_no like CONCAT('%',#{searchs.registerNo},'%')


    and s.card_no like CONCAT('%',#{searchs.cardNo},'%')


    and c.id = #{searchs.classId}


    and s.status = #{searchs.status}


    and s.school_id = #{searchs.school_id}

SELECT s.id, s.card_no, s.`name` AS name, s.register_no, s.sex, s.height, s.weight, s.ring_no, c.id AS class_id, c.title class_name, s.birthday, s.bind, s.status, FROM students s LEFT JOIN class c ON s.class_id = c.id where delete_flag = 0
and s.`name` like CONCAT('%',#{searchs.studentName},'%')
and s.register_no like CONCAT('%',#{searchs.registerNo},'%')
and s.card_no like CONCAT('%',#{searchs.cardNo},'%')
and c.id = #{searchs.classId}
and s.status = #{searchs.status}
and s.school_id = #{searchs.school_id}
${item.field} ${item['type']}
LIMIT #{startIndex}, #{pageSize}

SELECT count(s.id) FROM students s
LEFT JOIN account_student acs ON s.id = acs.student_id
LEFT JOIN class c ON s.class_id = c.id
where delete_flag = 0

    and s.`name` like CONCAT('%',#{searchs.studentName},'%')


    and s.register_no like CONCAT('%',#{searchs.registerNo},'%')


    and s.card_no like CONCAT('%',#{searchs.cardNo},'%')


    and c.id = #{searchs.classId}


    and s.bind = #{searchs.bind}


    and s.status = #{searchs.status}


    and acs.account_id = #{searchs.accountId}

SELECT s.id, s.card_no, s.`name` AS name, s.register_no, s.sex, s.height, s.weight, s.ring_no, c.id AS class_id, c.title class_name, s.birthday, s.bind, s.status, acs.relation_name, sc.id as school_id, sc.title as school_name, s.year FROM students s LEFT JOIN account_student acs ON s.id = acs.student_id LEFT JOIN class c ON s.class_id = c.id LEFT JOIN school sc ON c.school_id = sc.id where delete_flag = 0
and s.`name` like CONCAT('%',#{searchs.studentName},'%')
and s.register_no like CONCAT('%',#{searchs.registerNo},'%')
and s.card_no like CONCAT('%',#{searchs.cardNo},'%')
and c.id = #{searchs.classId}
and s.bind = #{searchs.bind}
and s.status = #{searchs.status}
and acs.account_id = #{searchs.accountId}
${item.field} ${item['type']}
LIMIT #{startIndex}, #{pageSize}

