mapper.select:
SELECT ac.id AS account_id, ac.nick_name, c.id AS classes_id, c.title AS classes_name, c.school_id, c.grade, s.title AS school_name, IF(ac.id=c.teacher_id,1,-1) AS is_head FROM account_class acc LEFT JOIN class c ON acc.class_id = c.id LEFT JOIN account ac ON acc.account_id = ac.id LEFT JOIN school s ON c.school_id=s.id WHERE 1=1
and ac.nick_name like CONCAT('%',#{searchs.teacherName},'%')
and ac.phone like CONCAT('%',#{searchs.phone},'%')
and IF(ac.id=c.teacher_id,1,-1) = #{searchs.isHead}
${item.field} ${item['type']}
LIMIT #{startIndex}, #{pageSize}

mapper.update:
update class set teacher_id = null
where teacher_id in

    #{teacherId}

mapper.select:
SELECT
    ac,
    teacher_name
FROM account ac
JOIN class c
ON ac.id = c.teacher_id
WHERE 1 = 1

    AND c.id = #{searchs.classId}


    ${item.field} ${item['type']}

LIMIT #{startIndex}, #{pageSize}
